import React,{Component} from 'react';
import './App.css';
import tasks from './sample/task.json';

console.log(tasks);


class App extends Component {
    state={
        tasks:tasks
    }
     render(){
         return(
             <div>
                 {this.state.tasks.map(e=><p>
                     {e.title} - {e.description} - {e.id}
                 </p>)}
             </div>
         )
     }
}

export default App;
